import { useEffect, useState } from 'react';
import React from 'react';

const Posts = () => {
  const [posts, setPosts] = useState([]);
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then((response) => response.json())
      .then((json) => setPosts(json));
  }, []);
  console.log(posts);
  return (
    <>
      <div className="container mainContainer">
        <div className="searchingContainer">
          <div className="input-group ">
            <input
              type="text"
              className="form-control"
              placeholder="Search by username"
              aria-label="earch by username"
              aria-describedby="basic-addon2"
              onChange={(e) => setSearchValue(e.target.value)}
            />
            <span className="input-group-text" id="basic-addon2">
              <img src={searchIcon} alt="search"></img>
            </span>
          </div>
        </div>
        <div className="sortingContainer">
          <span className="sortingText">Sorting by</span>
          <select
            onChange={(e) => setSortingValue(e.target.value)}
            className="form-select selecting"
            aria-label="Sorting by"
            value={sortingValue}
          >
            <option value="asc">asc</option>
            <option value="desc">desc</option>
          </select>
        </div>
        <div className="row row-cols-1 row-cols-sm-2 row-cols-md-2 row-cols-lg-3 ">
          {posts.map((el) => {
            return (
              <div className="col allCardsStyle" key={el?.id}>
                <div className="card" style={{ width: '18rem' }}>
                  <div className="card-body">
                    <h5 className="card-title">{el?.name}</h5>
                    <Link to="/posts" className="card-link">
                      See all posts
                    </Link>
                    <Link to="/albums" className="card-link">
                      See all albums
                    </Link>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};
export default Posts;
