import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import './cards.css';
import searchIcon from '../images/search-icon.svg';

const Card = () => {
  const [users, setUsers] = useState([]);
  const [sortingValue, setSortingValue] = useState('asc');
  const [searchValue, setSearchValue] = useState('');
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((response) => response.json())
      .then((json) => setUsers(json));
  }, []);

  const processData = useMemo(() => {
    return users
      .filter((user) => {
        return searchValue
          ? user?.name.toLowerCase().includes(searchValue.toLowerCase())
          : true;
      })
      .sort((user1, user2) =>
        sortingValue === 'asc'
          ? user1.name.localeCompare(user2.name)
          : user2.name.localeCompare(user1.name)
      );
  }, [sortingValue, users, searchValue]);

  return (
    <>
      <div className="container mainContainer">
        <div className="searchingContainer">
          <div className="input-group ">
            <input
              type="text"
              className="form-control"
              placeholder="Search by username"
              aria-label="earch by username"
              aria-describedby="basic-addon2"
              onChange={(e) => setSearchValue(e.target.value)}
            />
            <span className="input-group-text" id="basic-addon2">
              <img src={searchIcon} alt="search"></img>
            </span>
          </div>
        </div>
        <div className="sortingContainer">
          <span className="sortingText">Sorting by</span>
          <select
            onChange={setSortingValue}
            className="form-select selecting"
            aria-label="Sorting by"
          >
            <option value="1" defaultValue={true}>
              asc
            </option>
            <option value="2">desc</option>
          </select>
        </div>
        <div className="row row-cols-1 row-cols-sm-2 row-cols-md-2 row-cols-lg-3 ">
          {processData.map((el) => {
            return (
              <div className="col allCardsStyle" key={el?.id}>
                <div className="card" style={{ width: '18rem' }}>
                  <div className="card-body">
                    <h5 className="card-title">{el?.name}</h5>
                    <Link to="/posts" className="card-link">
                      See all posts
                    </Link>
                    <Link to="/albums" className="card-link">
                      See all albums
                    </Link>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default Card;
