import React, { useEffect, useMemo, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import './users-page.css';
import searchIcon from '../images/search-icon.svg';
import Sorting from './sorting';

const Users = () => {
  const { id } = useParams();
  const [users, setUsers] = useState([]);
  const [sortingValue, setSortingValue] = useState('asc');
  const [searchValue, setSearchValue] = useState('');
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((response) => response.json())
      .then((json) => setUsers(json));
  }, []);
  console.log(users);

  const processData = useMemo(() => {
    return users
      .filter((user) => {
        return searchValue
          ? user?.name.toLowerCase().includes(searchValue.toLowerCase())
          : true;
      })
      .sort((user1, user2) =>
        sortingValue === 'asc'
          ? user1.name.localeCompare(user2.name)
          : user2.name.localeCompare(user1.name)
      );
  }, [sortingValue, users, searchValue]);

  return (
    <>
      <div className="container mainContainer">
        <div className="searchingContainer">
          <div className="input-group ">
            <input
              type="text"
              className="form-control"
              placeholder="Search by username"
              aria-label="earch by username"
              aria-describedby="basic-addon2"
              onChange={(e) => setSearchValue(e.target.value)}
            />
            <span className="input-group-text" id="basic-addon2">
              <img src={searchIcon} alt="search"></img>
            </span>
          </div>
        </div>
        <Sorting sortingValue={sortingValue} />
        <div className="row row-cols-1 row-cols-sm-2 row-cols-md-2 row-cols-lg-3 ">
          {processData.map((el) => {
            return (
              <div className="col allCardsStyle" key={el?.id}>
                <div className="card shadow">
                  <div className="header">{el?.name}</div>
                  <div className="body">
                    <div className="buttons-container">
                      <Link to={`/posts/${el.id}`} className="card-link">
                        <button className="buttonLink">See all posts</button>
                      </Link>
                      <Link to={`/albums/${el.id}`} className="card-link">
                        <button className="buttonLink">See all albums</button>
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default Users;
